mapper.update:
update comment
set content = #{content}
where id = #{id}

mapper.select:
select IFNULL(MAX(comment_group+1), 1)
from comment
where board_id = #{boardId}

select comment_group
from comment
where id = #{parentId}

select IFNULL(MAX(sequence+1), 1)
from comment
where board_id = #{boardId} AND comment_group = #{commentGroup}
